				GameMessage("Function is locked", 9);
				return;
			}
			bool flag = false;
			if (base.Player_FLD_Map != World.Map_Func_Offline)
			{
				GameMessage("ChiÒ coì thêÒ sýÒ duòng õÒ baÒn ðôÌ: " + CoordinateClass.getmapname(World.Map_Func_Offline), 9);
