		public void 百宝(byte[] data, int length)
		{
			if (元宝合成开关 == 1)
			{
				return;
			}
			byte[] array = new byte[4];
			Buffer.BlockCopy(data, 11, array, 0, 1);
			switch (BitConverter.ToInt32(array, 0))
			{
